function results_figure_matlab(YMatrix1, filename)
%CREATEFIGURE(YMATRIX1)
%  YMATRIX1:  matrix of y data

%  Auto-generated by MATLAB on 20-Feb-2016 21:34:00

% Create figure
figure1 = figure('InvertHardcopy','off','Color',[1 1 1]);

% Create axes
axes1 = axes('Parent',figure1);
hold(axes1,'on');

% Create multiple lines using matrix input to plot
plot(YMatrix1,'MarkerSize',8,'Marker','o','LineWidth',2);

% Create xlabel
xlabel('SNR ');

% Create ylabel
ylabel({'% Correct words','(100% = no mix)'});

% Uncomment the following line to preserve the X-limits of the axes
% xlim(axes1,[1 10]);
% Uncomment the following line to preserve the Y-limits of the axes
% ylim(axes1,[0 120]);
% Uncomment the following line to preserve the Z-limits of the axes
% zlim(axes1,[-1 1]);
% Set the remaining axes properties
set(axes1,'FontSize',14,'XTickLabel',...
    {'0','1','2','5','10','15','20','30','40','inf'},'YGrid','on');

savefig(filename)
